body[data-theme="dark"] {
  background-color: $backgroundBlack;

  .header {
    &__wrapper {
      .header__headline {
        color: $textWhite;
      }

      .header__buttonsBox {
        .header__searchButton {
          background: $buttonsGrey url(../images/search.svg) no-repeat center;
        }

        .header__themeButton {
          background: $buttonsGrey url(../images/lightThemeSun.svg) no-repeat center;
        }
      }
    }

    &__searchBarBox {
      background-color: $backgroundBlack;

      .header__searchBar {
        background-color: $buttonsGrey;
        color: $textWhite;

        &::placeholder {
          color: $textWhite;
        }
      }

      .header__searchBarButton {
        background: url(../images/search.svg) center;
      }

      .header__closeButton {
        background: url(../images/close.svg) center;
      }
    }

    &__editorButtonBox {
      background-color: $backgroundBlack;

      .header__backButton {
        background: $buttonsGrey url(../images/back.svg) no-repeat center;
      }

      .header__buttonsWrapper {
        .header__saveButton {
          background: $buttonsGrey url(../images/save.svg) no-repeat center;
          background-size: 25px 25px;
          background-blend-mode: lighten;
        }
      }
    }

    &__rewriteEditor {
      .header__rewriteSaveButton {
        background: $buttonsGrey url(../images/save.svg) no-repeat center;
        background-size: 25px 25px;
        background-blend-mode: lighten;
      }
    }
  }

  .main {
    &__emptyContent {
      .main__paragraph {
        color: $textWhite;
      }
    }

    &__notFoundContent {
      .main__paragraphNotFound {
        color: $textWhite;
      }
    }

    &__buttonBox {
      .main__createButton {
        background: $backgroundBlack url(../images/add.svg) no-repeat center;
      }
    }

    &__form {
      background-color: $backgroundBlack;

      .main__formTitle {
        color: $textWhite;

        &::placeholder {
          color: $buttonsGrey;
        }
      }

      .main__formText {
        color: $textWhite;

        &::placeholder {
          color: $buttonsGrey;
        }
      }
    }
  }

  .formContainer {
    background-color: $backgroundBlack;

    .header__editorButtonBox {
      background-color: $backgroundBlack;

      .header__backButton {
        background: $buttonsGrey url(../images/back.svg) no-repeat center;
      }

      .header__buttonsWrapper {
        .header__saveButton {
          background: $buttonsGrey url(../images/save.svg) no-repeat center;
          background-size: 25px 25px;
          background-blend-mode: none;
        }
      }
    }

    .main__form {
      background-color: $backgroundBlack;

      .main__formTitle {
        color: $textWhite;

        &::placeholder {
          color: $buttonsGrey;
        }
      }

      .main__formText {
        color: $textWhite;

        &::placeholder {
          color: $buttonsGrey;
        }
      }
    }
  }

  .editFormContainer {
    background-color: $backgroundBlack;

    .header__rewriteEditor {
      .header__rewriteBackButton {
        background: $buttonsGrey url(../images/back.svg) no-repeat center;
      }

      .header__rewriteSaveButton {
        background: $buttonsGrey url(../images/save.svg) no-repeat center;
        background-size: 25px 25px;
      }
    }

    .main__editForm {
      .main__editTitle {
        &::placeholder {
          color: $buttonsGrey;
        }
      }

      .main__editText {
        &::placeholder {
          color: $buttonsGrey;
        }
      }
    }
  }

  .deleteMessageWrapper {
    .deleteMessage {
      background-color: $backgroundBlack;

      .deleteMessageP {
        color: $textWhite;
      }

      .buttonWrapper {
        .buttonFromDelMessage {
          color: $textWhite;
        }
      }
    }
  }
}

