* {
  margin: 0;
  padding: 0;
  font-family: "Nunito", sans-serif;
}

body {
  position: relative;
  display: flex;
  flex-direction: column;
  gap: 22px;
}

.header {
  position: sticky;
  top: 0;
  z-index: 1;
  width: 370px;
  height: 106px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 128px;
  margin: 0 auto 0 auto;
  background-color: inherit;
}
.header__wrapper {
  width: 365px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 126px;
  margin-top: 10px;
}
.header__wrapper .header__headline {
  font-size: 43px;
  font-weight: 600;
  line-height: 58.65px;
  text-align: left;
}
.header__wrapper .header__buttonsBox {
  display: flex;
  gap: 21px;
}
.header__wrapper .header__buttonsBox .header__searchButton {
  height: 50px;
  width: 50px;
  border-radius: 15px;
  border: none;
  transition: 0.3s;
}
.header__wrapper .header__buttonsBox .header__searchButton:hover {
  transform: scale(1.2);
}
.header__wrapper .header__buttonsBox .header__themeButton {
  height: 50px;
  width: 50px;
  border-radius: 15px;
  border: none;
  transition: 0.3s;
}
.header__wrapper .header__buttonsBox .header__themeButton:hover {
  transform: scale(1.2);
}
.header__searchBarBox {
  position: absolute;
  right: 1px;
  top: 33px;
  background-color: rgb(37, 37, 37);
  display: none;
}
.header__searchBarBox::-webkit-search-cancel-button {
